import { expect } from 'chai';
import aniCal from "../easeInExpo.mjs";
describe('Test easeInExpo', function () {
  it("basic test", function () {
    var start = 0;
    var to = 100;
    var duration = 5;
    var actual1 = aniCal(1, start, to, duration).toFixed(2);
    var actual2 = aniCal(2, start, to, duration).toFixed(2);
    var actual3 = aniCal(3, start, to, duration).toFixed(2);
    var actual4 = aniCal(4, start, to, duration).toFixed(2);
    var actual5 = aniCal(5, start, to, duration).toFixed(2);
    expect({
      actual1: "0.39",
      actual2: "1.56",
      actual3: "6.25",
      actual4: "25.00",
      actual5: "100.00"
    }).to.deep.equal({
      actual1,
      actual2,
      actual3,
      actual4,
      actual5
    });
  });
});