import { expect } from "chai";
import { getTimestamp } from "../index.mjs";
describe("Test getTimestamp", function () {
  var now = new Date().getTime();
  it("defined time", function () {
    var ut = getTimestamp(now);
    expect(ut).to.equal(now);
  });
  it("get new time", function () {
    var ut = getTimestamp();
    expect(ut > now).to.be.true;
  });
});