import { expect } from "chai";
import randKey, { getSN } from "../index.mjs";
describe("Test rand key", function () {
  it("get key", function () {
    var keys = randKey().split(".");
    expect(isNaN(keys[0])).to.false;
    expect(isNaN(keys[1])).to.false;
  });
});
describe("Test get SN", function () {
  it("basic test", function () {
    var n1 = getSN();
    var n2 = getSN();
    expect(n1).to.equal("_0");
    expect(n2).to.equal("_1");
  });
  it("get sn with name", function () {
    var n1 = getSN("foo");
    var n2 = getSN("foo");
    expect(n1).to.equal("foo_0");
    expect(n2).to.equal("foo_1");
  });
});