import { expect } from "chai";
import { jsdom, cleanIt } from "reshow-unit-dom";
import { win, doc, hasWin } from "../index.mjs";
describe("Test doc in node", function () {
  before(function () {
    jsdom();
    document = undefined;
    window = undefined;
  });
  after(function () {
    return cleanIt();
  });
  it("test doc", function () {
    var d = doc();
    expect(d.__null).to.be.true;
  });
});
describe("Test win in node", function () {
  before(function () {
    jsdom();
    document = undefined;
    window = undefined;
  });
  after(function () {
    return cleanIt();
  });
  it("test win", function () {
    var w = win();
    expect(w.__null).to.be.true;
  });
  it("test has win", function () {
    var w = win();
    expect(hasWin()).to.be.false;
  });
});